import React, {useState, useEffect} from 'react';
import './services/api'
import './global.css';
import './App.css';
import './Sidebar.css';
import './Main.css';

function App() {

const [github_username, setGithubUsername] = useState('');
const [techs, setTechs] = useState('');
const [latitude, setLatitude] = useState('');
const [longitude, setLongitude] = useState('');

useEffect(() => {
	navigator.geolocation.getCurrentPosition(
		(position) => {
			const {latitude, longitude} = position.coords;

			setLatitude(latitude);
			setLongitude(longitude);
		},
		(err) => {
			console.log(err);
		},
		{
			timeout: 30000
		}
	);
}, []);

async function handleAddDev(e){
	e.preventDefault();

	const response = await api.post('/devs', {
		github_username,
		techs,
		latitude,
		longitude
	});
}

return (
<div id="app">
	<aside>
		<strong>Cadastrar</strong>

		<form onSubmit={handleAddDev} >
			<div className="input-block">
				<label htmlFor="github_username">Usuário do Github</label>
				<input name="github_username" id="github_username" required value={github_username}
				onChange={e => setGithubUsername(e.target.value)} />
			</div>

			<div className="input-block">
				<label htmlFor="techs">Tecnologias</label>
				<input name="techs" id="techs" required value={techs}
				onChange={e => setTechs(e.target.value)} />
			</div>



			<div className="input-group">
				<div className="input-block">
					<label htmlFor="latitude">Latitude</label>
					<input type="number" name="latitude" id="latitude" required value={latitude} 
					onChange={e => setLatitude(e.target.value)} />
				</div>
				<div className="input-block">
					<label htmlFor="longitude">Longitude</label>
					<input type="number" name="longitude" id="longitude" required value={longitude}
					onChange={e => setLongitude(e.target.value)} />
				</div>
			</div>

			<button type="submit">Salvar</button>

		</form>

	</aside>

	<main>
		<ul>
			<li className="dev-item" >
				<header>
					<img src="https://avatars2.githubusercontent.com/u/45232536?s=460&v=4" alt="Guilherme Rocha" />
					<div className="user-info">
						<strong>Guilherme Rocha</strong> 
						<span>PHP, JavaScript</span>
					</div>
				</header>
				<p>Fascinado pela constante evolução da Tecnologia de Informação e absolutamente compromissado a contribuir para o compartilhamento de experiências e ideias.</p>
				<a href="https://github.com/guilhermeDTNA">Acessar perfil no GitHub</a>
			</li>
			<li className="dev-item" >
				<header>
					<img src="https://avatars2.githubusercontent.com/u/45232536?s=460&v=4" alt="Guilherme Rocha" />
					<div className="user-info">
						<strong>Guilherme Rocha</strong>
						<span>PHP, JavaScript</span>
					</div>
				</header>
				<p>Fascinado pela constante evolução da Tecnologia de Informação e absolutamente compromissado a contribuir para o compartilhamento de experiências e ideias.</p>
				<a href="https://github.com/guilhermeDTNA">Acessar perfil no GitHub</a>
			</li>
			<li className="dev-item" >
				<header>
					<img src="https://avatars2.githubusercontent.com/u/45232536?s=460&v=4" alt="Guilherme Rocha" />
					<div className="user-info">
						<strong>Guilherme Rocha</strong>
						<span>PHP, JavaScript</span>
					</div>
				</header>
				<p>Fascinado pela constante evolução da Tecnologia de Informação e absolutamente compromissado a contribuir para o compartilhamento de experiências e ideias.</p>
				<a href="https://github.com/guilhermeDTNA">Acessar perfil no GitHub</a>
			</li>
			<li className="dev-item" >
				<header>
					<img src="https://avatars2.githubusercontent.com/u/45232536?s=460&v=4" alt="Guilherme Rocha" />
					<div className="user-info">
						<strong>Guilherme Rocha</strong>
						<span>PHP, JavaScript</span>
					</div>
				</header>
				<p>Fascinado pela constante evolução da Tecnologia de Informação e absolutamente compromissado a contribuir para o compartilhamento de experiências e ideias.</p>
				<a href="https://github.com/guilhermeDTNA">Acessar perfil no GitHub</a>
			</li>
		</ul>
	</main>
</div>
);
}

export default App;
